#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="$basedir\D:\软件专业\Uniapp\easyjob-app\node_modules\.store\@dcloudio+vite-plugin-uni@3.0.0-3090420231025001\node_modules\@dcloudio"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="$basedir/D:/软件专业/Uniapp/easyjob-app/node_modules/.store/@dcloudio+vite-plugin-uni@3.0.0-3090420231025001/node_modules/@dcloudio"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$env_node_path$pathsep$new_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../.store/@dcloudio+vite-plugin-uni@3.0.0-3090420231025001/node_modules/@dcloudio/vite-plugin-uni/bin/uni.js" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../.store/@dcloudio+vite-plugin-uni@3.0.0-3090420231025001/node_modules/@dcloudio/vite-plugin-uni/bin/uni.js" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../.store/@dcloudio+vite-plugin-uni@3.0.0-3090420231025001/node_modules/@dcloudio/vite-plugin-uni/bin/uni.js" $args
  } else {
    & "node$exe"  "$basedir/../.store/@dcloudio+vite-plugin-uni@3.0.0-3090420231025001/node_modules/@dcloudio/vite-plugin-uni/bin/uni.js" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
